create function dba.fnGetNumberFromString( @strInput varchar(255) ) 
returns varchar(255)
as
begin
  declare @intNumber integer
  set @intNumber = PATINDEX('%[^0-9]%',@strInput)
  while @intNumber > 0
    begin
      set @strInput = STUFF(@strInput,@intNumber,1,'')
      set @intNumber = PATINDEX('%[^0-9]%',@strInput)
    end
  return ISNULL(@strInput,0)
end
