create function DBA.f_get_item_reserved_qty( in as_item varchar(50),in an_center integer,in an_location integer,in an_store integer,in as_transId nvarchar(10) default '0' )  /* @parameter_name parameter_type [= default_value], ... */
returns numeric(20,7)
begin
  declare ldc_flag char(1);
  declare ldc_qty decimal(20,7);
  declare ldc_wsReservationQty decimal(20,7);
  declare ldc_wsReceiptionQty decimal(20,7);
  declare ldc_scQuotationQty decimal(20,7);
  //
  select f_get_about('sc_qty_reserved') into ldc_flag;
  if ldc_flag <> 'Y' then
    return 0
  end if;
  //1 WS Reservation
  select Sum(ws_reservationoperationitem.qty)
    into ldc_wsReservationQty from ws_reservation,ws_reservationoperationitem
    where(ws_reservation.reservationid = ws_reservationoperationitem.reservationid)
    and(ws_reservation.service_center = ws_reservationoperationitem.service_center)
    and(ws_reservation.location_id = ws_reservationoperationitem.location_id)
    and(ws_reservation.delete_flag <> 'Y')
    and(ws_reservation.reserve_date >= today())
    and(ws_reservation.status = 'R')
    and(ws_reservation.service_center = an_center)
    and(ws_reservation.location_id = an_location)
    and(ws_reservationoperationitem.itemid = as_item);
  //
  if ldc_wsReservationQty is null then
    set ldc_wsReservationQty = 0
  end if;
  //2 WS Reciption
  if ldc_wsReceiptionQty is null then
    set ldc_wsReceiptionQty = 0
  end if;
  //3 SC Quotation
  select Sum(sc_quotation_detail.qty)
    into ldc_scQuotationQty from sc_quotation_detail,sc_quotation_header
    where(sc_quotation_detail.debit_header = sc_quotation_header.debit_header)
    and(sc_quotation_detail.service_center = sc_quotation_header.service_center)
    and(sc_quotation_detail.location_id = sc_quotation_header.location_id)
    and(sc_quotation_header.service_center = an_center)
    and(sc_quotation_header.location_id = an_location)
    and(sc_quotation_detail.item_id = as_item)
    and(sc_quotation_header.status in( 'A','P' ) )
    and(sc_quotation_header.qty_reserved = 1)
    and(sc_quotation_header.confirm_date >= today())
    and(sc_quotation_detail.debit_header <> as_transId)
    and(not sc_quotation_header.debit_header = any(select distinct quotation_no from DBA.sc_debit_header where service_center = sc_quotation_header.service_center and location_id = sc_quotation_header.location_id and quotation_no is not null));
  //
  if ldc_scQuotationQty is null then
    set ldc_scQuotationQty = 0
  end if;
  //
  set ldc_qty = ldc_wsReservationQty+ldc_wsReceiptionQty+ldc_scQuotationQty;
  //
  return ldc_qty
end
go

create function DBA.f_get_item_credit_interval( in as_item varchar(50),in an_center integer,in an_location integer,in an_store integer,in ad_fdate datetime default today(),in ad_tdate datetime default today() ) 
returns numeric(20,7)
--V1.0 get credit in interval
begin
  declare ldc_credit decimal(20,7);
  declare ldc_return decimal(20,7);
  declare ldc_trans_to decimal(20,7);
  declare ldc_bal decimal(20,7);
  // 
  //Credit
  select Sum(sc_credit_detail.qty)
    into ldc_credit from sc_credit_detail
      ,sc_credit_header
    where(sc_credit_detail.credit_header = sc_credit_header.credit_header)
    and(sc_credit_header.service_center = sc_credit_detail.service_center)
    and(sc_credit_detail.location_id = sc_credit_header.location_id)
    and(sc_credit_detail.item_id = as_item)
    and(sc_credit_header.store_id = an_store or an_store = 0)
    and(sc_credit_header.service_center = an_center)
    and(sc_credit_header.location_id = an_location)
    and(Datetime(credit_date+trans_time) >= ad_fdate)
    and(Datetime(credit_date+trans_time) <= ad_tdate);
  //Return
  select sum(sc_ret_detail.qty)
    into ldc_return from sc_ret_detail
      ,sc_ret_header
    where(sc_ret_detail.credit_header = sc_ret_header.credit_header)
    and(sc_ret_header.service_center = sc_ret_detail.service_center)
    and(sc_ret_header.location_id = sc_ret_detail.location_id)
    and(sc_ret_detail.item_id = as_item)
    and(sc_ret_header.store_id = an_store or an_store = 0)
    and(sc_ret_header.service_center = an_center)
    and(sc_ret_header.location_id = an_location)
    and(Datetime(credit_date+trans_time) >= ad_fdate)
    and(Datetime(credit_date+trans_time) <= ad_tdate);
  //Trans to 
  select Sum(sc_transfer_detail.qty)
    into ldc_trans_to from sc_transfer_detail
      ,sc_transfer_header
    where(sc_transfer_detail.credit_header = sc_transfer_header.credit_header)
    and(sc_transfer_header.service_center = sc_transfer_detail.service_center)
    and(sc_transfer_header.location_id = sc_transfer_detail.location_id)
    and(sc_transfer_header.arrival_flag = 'Y')
    and(sc_transfer_detail.item_id = as_item)
    and(sc_transfer_header.store_id_to = an_store or an_store = 0)
    and(sc_transfer_header.service_center = an_center)
    and(sc_transfer_header.location_id_to = an_location)
    and(Datetime(credit_date+trans_time) >= ad_fdate)
    and(Datetime(credit_date+trans_time) <= ad_tdate);
  //
  set ldc_credit = IsNull(ldc_credit,0);
  set ldc_return = IsNull(ldc_return,0);
  set ldc_trans_to = IsNull(ldc_trans_to,0);
  set ldc_bal = (ldc_credit+ldc_return+ldc_trans_to);
  set ldc_bal = IsNull(ldc_bal,0);
  return ldc_bal
end
go

create function ////////////////////////////
DBA.f_get_invoice_cost_oil( 
  in as_InvoiceID varchar(50),
  in an_InvoiceType integer,
  in an_center integer,
  in an_location integer ) 
returns numeric(20,7)
--Ver 1.0 get oil cost only (copy from f_get_invoice_cost )
begin
  declare as_invoice_nature varchar(20);
  declare as_joborder_id varchar(20);
  declare an_debit_header integer;
  declare an_credit_header integer;
  declare an_debit numeric(20,7);
  declare an_return numeric(20,7);
  declare an_cost numeric(20,7);
  select invoice_nature,joborder_id,debit_header,credit_header
    into as_invoice_nature,as_joborder_id,an_debit_header,an_credit_header
    from ws_invoiceheader
    where invoiceid = as_InvoiceID
    and invoicetype = an_InvoiceType
    and service_center = an_center
    and location_id = an_location;
  if as_invoice_nature = 'R' and(as_joborder_id is null or as_joborder_id = '') then
    select Sum(sc_ret_detail.cost_price*sc_ret_detail.qty)
      into an_cost from sc_ret_detail
        left outer join sc_item
        on sc_ret_detail.item_id = sc_item.item_id
