      current_location_type,
      check_type,
      bank_name,
      vend_code ) values
      ( @serial,
      ar_check_num,
      ar_issue_date,
      ar_due_date,
      ar_bank_id,
      ar_brand,
      ar_service_center,
      ar_location_id,
      ar_module,
      ar_receipt_id,
      ar_doc_t_num,
      ar_doc_type,
      ar_customer_id,
      ar_check_value,
      ar_log_store,
      ar_issue_location,
      ar_current_location,
      ar_status,
      ar_payment_value,
      ar_current_location_type,
      ar_check_type,
      ar_bank_name,
      ar_vend_code ) 
  elseif ar_optype = 'U' then
    update checks
      set check_num = ar_check_num,
      issue_date = ar_issue_date,
      due_date = ar_due_date,
      bank_id = ar_bank_id,
      brand = ar_brand,
      service_center = ar_service_center,
      module = ar_module,
