      where doc_t_num = ar_doc_t_num
      and doc_type = ar_doc_type
      and service_center = ar_service_center
      and location_id = ar_location_id
  elseif ar_optype = 'D' then
    delete from checks where doc_t_num = ar_doc_t_num
      and doc_type = ar_doc_type
      and service_center = ar_service_center
      and location_id = ar_location_id
