        line_code,
        date_ending,
        depreciation,
        notes,
        posted ) values
        ( new_name.asset_code,
        new_name.branch_id,
        li_Index,
        "Date"(str(li_Year)+'-'+str(li_Index)+'-'+str(li_Days)),
        ldc_Dep,
        '',
        'N' ) ;
      set li_Index = li_Index+1
    end loop
  end if
